import React, { useEffect, useState } from 'react';

function Photo(props){
    let [arr, setPhoto] = useState([]);
let [photoId,setPhotoId]=useState(((props.album.id*50)-50)+1);
let tmpPhotoArr=[];
        useEffect(function  getData()
        {
            let url = `https://jsonplaceholder.typicode.com/photos?_page`;
            fetch(url)
                .then(response => response.json())
                .then(response => {
                    tmpPhotoArr=arr;
                    tmpPhotoArr.push(response);
                    setPhoto(tmpPhotoArr);
                    console.log(arr);
                })
            },[photoId]);
        function changeId(){
            console.log(photoId ,props.album.id)
            if(photoId<(props.album.id*50)){
                setPhotoId(photoId+1);
            }
            else{
                alert("you finish load all the album's pictures");
            }
        }
return(
<div>
<h1>album {props.album.id}</h1>
{
arr.map((photo)=>(
    <span key={photo[0].id}>
<h3>{photo[0].title}</h3>
<img src={photo[0].thumbnailUrl} height="150px" width="150px"></img>
</span>
))} 
<button onClick={changeId} id="loadedButton" >load more</button>
</div>
);
}
export default Photo;
