import React from 'react';
import { useState ,useEffect} from 'react';
import { useParams } from 'react-router';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Loader from './Loader';

const AddPost = ({ handlePostUpdate ,postArr}) => {
    let params=useParams();
    const [isLoading, setIsLoading] = useState(false); //true when the server is up
    const [open, setOpen] = React.useState(false); //open the dialog form
    const [title, setTitle] = useState('') //take the new-post-title text
    const [body, setBody] = useState('')  //take the new-post-body text
    const [newID, setNewID] = useState(null);
    const onFinish = async () => {
      setIsLoading(true)
      setTitle("");
      setBody("");
      let lastID = postArr.length ? postArr[postArr.length - 1].id : 0;

      // Optimistic update
      setNewID(lastID + 1);

      try {
        const post = {
          id: newID,
          title: title,
          body: body,
          userId: params.userID,
        };
        const response = await fetch('https://jsonplaceholder.typicode.com/posts', {
          method: 'POST',
          body: JSON.stringify(post),
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
          },
        });
  
        if (!response.ok) {
          throw new Error(`HTTP error ${response.status}`);
        }
        const newPost = await response.json();
        newPost.id=post.id;
        handlePostUpdate(newPost); // Call the function to update posts
        handleClose();
      } catch (error) {
        console.error('ERROR:', error); // Log the error to the console
        // Optionally, display an error message to the user here
      }
      setIsLoading(false);
    };
    
    const handleClickOpen = () => { //open dialog form
      setOpen(true);
    };
  
    const handleClose = () => {  //close the dialog form
      setOpen(false);
    };
    useEffect(() => {
      if (postArr.length) {
        setNewID(postArr[postArr.length - 1].id + 1);
      }
    }, [postArr]);
    return (
        <div className="addPostForm">
          <React.Fragment>
              <Button variant="outlined" onClick={handleClickOpen}>
                Add new post
              </Button>
             <Dialog open={open} onClose={handleClose} >
                <DialogTitle>Add new post</DialogTitle>
                 <DialogContent>
                 <DialogContentText>
                 Please fill in the title and content of the post to be published.
                </DialogContentText>
                <TextField
                  autoFocus
                  margin="dense"
                  id="name"
                  label="title"
                  type="text"
                  fullWidth
                  variant="standard"
                  value={title}
                   onChange={e => {
                    setTitle(e.target.value)
                  }}
                 />
                <TextField
                autoFocus
                margin="dense"
                id="body"
                label="content"
                type="text"
                fullWidth
                variant="standard"
                value={body}
                onChange={e => {
                setBody(e.target.value)
                }}
               />
          </DialogContent>
           <DialogActions>
             <Button onClick={handleClose}>Cancel</Button>
             <Button onClick={onFinish}>Subscribe</Button>
             {isLoading&&<Loader/>}
           </DialogActions>
      </Dialog>
    </React.Fragment>
      </div>
    );
  };
  
  export default AddPost
