import React, { useEffect, useState } from 'react';


function Post(props) {
    
    let [comments, setComments] = useState([]);
    let [arr, setArr] = useState([]);
    function getData()
    {
        let url = `https://jsonplaceholder.typicode.com/comments?postId=${props.post.id}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                setComments(response);
            })
        }
    useEffect(()=>{getData()},[])
    function show(){
        setArr(comments);
        
    }
    return (
        <div>
            <p> {props.post.body}</p>
<button onClick={show}>show comments</button>
{
    arr.map((comment)=>(
    <h4 key={comment.id}>{comment.body}</h4>
))}
    </div>
    );
}
export default Post;
