import React, { useEffect, useState } from 'react';
import SinglePhoto from './SinglePhoto';
import { useParams } from 'react-router';
import {useNavigate} from "react-router-dom";

function Photo(){
    let navigate=useN
    let param=useParams();
    console.log(param);
    let [arr, setPhoto] = useState([]);
let [photoId,setPhotoId]=useState(((param.albumId*50)-50)+1);
let tmpPhotoArr=[];

useEffect(
    () => {
      if ((localStorage.getItem("userName")) == 'undefined' || (localStorage.getItem("userName")) == null) {
        console.log('not found');
        navigate("/");
      }
      else {
        let url = `https://jsonplaceholder.typicode.com/photos?_page=${photoId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                tmpPhotoArr=arr;
                tmpPhotoArr.push(response);
                setPhoto(tmpPhotoArr);
                console.log(arr);
            })
      }
    }, [photoId]
  );
        function changeId(){
            console.log(photoId ,param.albumId)
            if(photoId<(param.albumId*50)){
                setPhotoId(photoId+10);
            }
            else{
                alert("you finish load all the album's pictures");
            }
        }
return(
<div>
<h1>album {param.albumId}</h1>
{
arr.map((photo)=>(
 <SinglePhoto single={photo} key={photo[0].id}>
     </SinglePhoto>
))} 
<button onClick={changeId} id="loadedButton" >load more</button>
</div>
);
}
export default Photo;
