import React, { useState ,useEffect} from 'react'
import Main from './components/Main'
import Posts from './components/Posts';
import { Routes, Route ,Router,HashRouter,useParams} from 'react-router-dom';
import AddPost from './components/AddPost';

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [album, setAlbumId] = useState();
    const [post, setPost] = useState();
    const { userID } = useParams();
const [prevUserID, setPrevUserID] = useState();

  useEffect(() => {
    if (userID !== prevUserID) {
      setCurrentUser(userID);
      setPrevUserID(userID);
    }
  }, [userID, prevUserID]);
    function handleCurrent(data) {
        setCurrentUser(data);
    }
    function handleAlbum(data) {
        setAlbumId(data);
    }
    function handlePost(data) {
        setPost(data);
    }
    return (
        <div>
            
            <Routes>
        <Route
          path="/"
          element={<Main setCurrentUser={setCurrentUser} />}
        />
        <Route
          path="/:userID/Posts"
          element={<Posts currentUser={currentUser} setPost={setPost} />}
        />
        <Route
          path="/:userID/Posts/addPost"
          element={<AddPost currentUser={currentUser} post={post} />}
        />
      </Routes>
                
        </div>
    );
}
export default App
