import React, { useState, useEffect } from 'react';
import Main from './components/Main';
import Posts from './components/Posts';
import { Routes, Route, Router, HashRouter, useParams } from 'react-router-dom';
import AddPost from './components/AddPost';
function App() {
const [posts, setPosts] = useState([]);
  const [currentUser, setCurrentUser] = useState();

  const  getFetchPosts=(IDuser) =>{
    let url = `https://jsonplaceholder.typicode.com/posts?userId=${IDuser}`;
            fetch(url)
                .then(response => response.json())
                .then(response => {
                    console.log(response,"response")
                    setPosts(response);
                })
    console.log(posts,"posts in app")
    return posts;
  }
  function handlePostUpdate (newPost) {
    // This function now accepts a new post as an argument
    setPosts([...posts, newPost]); // Append new post to array
  };
  const { userID } = useParams();

  useEffect(() => {
    setCurrentUser(userID);
  }, [userID]);

  function handleCurrent(data) {
    setCurrentUser(data);
  }
  return (
    <div>
      <Routes>
        <Route path="/" element={<Main handleCurrent={handleCurrent} />} />
        <Route
          path="/:userID/Posts"
          element={
            <div>
              <Main handleCurrent={handleCurrent} getFetchPosts={getFetchPosts}/>
              <Posts currentUser={currentUser} />
            </div>
          }
        />
        <Route
          path="/:userID/Posts/addPost"
          element={
            <div>
              <Main handleCurrent={handleCurrent} />
              <Posts currentUser={currentUser}  getFetchPosts={getFetchPosts}/>
              <AddPost currentUser={currentUser} handlePostUpdate={handlePostUpdate} /> 
            </div>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
