import React, { useState } from 'react';
import Main from'./Main';
import {useNavigate} from "react-router-dom";


function LogIn(props) {
    let navigate = useNavigate();
    const [userName, setUserName] = useState('')
    const [userPassword, setuserPassword] = useState('')
    function fetchItems() {
        let url = `https://jsonplaceholder.typicode.com/users?username=${userName}`;
        console.log(url);
        fetch(url)
            .then(response => response.json())
            .then(response => {
                if(response[0].address.geo.lat.split('.')[1]==userPassword){
                    localStorage.setItem("userName",JSON.stringify(response) );
                    console.log("work");
                    navigate(`/${response[0].id}/Main`);
                }
               else{
                alert('wrong password')
               }
            }).catch(response => {
               alert('user not exist')
            })
    }
    return (
        <div className="logIn">
            <div className="logField">
            <input name="userName" type="text" value={userName} onChange={(e) => setUserName(e.target.value)}></input>
            <input type="password" value={userPassword} onChange={(e) => setuserPassword(e.target.value)}></input>
            <button onClick={fetchItems}>log in</button>
            </div>
        </div>
    );
}
export default LogIn;
