import React, { useState } from 'react'
import Main from './components/Main'
import Posts from './components/Posts';
import { Routes, Route ,Router,HashRouter} from 'react-router-dom';
import AddPost from './components/AddPost';

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [album, setAlbumId] = useState();
    const [post, setPost] = useState();
    function hundleCurrent(data) {
        setCurrentUser(data);
    }
    function hundleAlbum(data) {
        setAlbumId(data);
    }
    function hundlePost(data) {
        setPost(data);
    }
    return (
        <div>
            
            <Routes>
      <Route exact path="/" component={Main} />
      <Route path="/:userID/Posts" children={[
        <Posts currentUser={currentUser} hundlePost={hundlePost} />,
        <AddPost currentUser={currentUser} hundlePost={hundlePost} />,
      ]} />
    </Routes>
                
        </div>
    );
}
export default App
