import React, { Component } from 'react';
import Game from './game';

class Gamer extends Component {


    render() {
        console.log()
        return (
            <React.Fragment>
       
                <Game
                    name={this.props.namePlayer}
                    score={this.props.score}
                    counterStepsFunc={this.props.counterStepsFunc}
                />
                         <h3>steps: {this.props.counterSteps}</h3>
            </React.Fragment>
        );
    }
}
export default Gamer;
