import React from 'react';
import {useNavigate} from "react-router-dom";
import {useEffect, useState } from 'react';


function Info(){
    
    const [currentUser, setCurrentUser] = useState({id:'',name:'',address:{street:'',},email:'',phone:'',website:'',company:''});
    let navigate = useNavigate();
    useEffect(
      () => {
        if ((localStorage.getItem("current")) == 'undefined' || (localStorage.getItem("current")) == null) {
          console.log('not found');
          navigate("/");
        }
        else {
            setCurrentUser((JSON.parse(localStorage.getItem('current')))[0]);
        }
      }, []
    );
    const handle = (param) => {
      if (currentUser == null || currentUser == 'undefined')
        return "not log in";
      else {
        if (param=="address") {
          if (param == "geo")
            return currentUser.address.geo;
          return currentUser.address;
        }
        else if (param == "company")
          return currentUser.company;
        return currentUser;
      }
  
    }
 return(
<div>
<h3>name: {currentUser.name}</h3>
<h3>address: {currentUser.address.street} {currentUser.address.suite} {currentUser.address.city}</h3>
<h3>email: {currentUser.email}</h3>
<h3>phone: {currentUser.phone}</h3>
<h3>website: {currentUser.website}</h3>
<h3>company name: {currentUser.company.name}</h3>
<h3>company catchPhrase: {currentUser.company.catchPhrase}</h3>
<h3>company bs: {currentUser.company.bs}</h3>

</div>
);
}
export default Info;
