import React, {useEffect, useState } from 'react';
import { Link } from 'react-router-dom';



function Albums()
{
    let [albums, setAlbums] = useState([]);
    function getData()
    {
        let userId=(JSON.parse(localStorage.getItem('userName')))[0].id;
        let url = `https://jsonplaceholder.typicode.com/albums?userId=${userId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                setAlbums(response);
            })
    }
    useEffect(()=>{getData()},[])
return(
<div>
{
albums.map((album)=>(
    <div key={album.id}>
<Link to={`/Main/Albums/photo/${album.id}`} >{album.title}</Link>
</div>
))}
</div>
);
}
export default Albums;
