import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

function Post() {
    let param=useParams();
    let [comments, setComments] = useState([]);
    let [arr, setArr] = useState([]);
    let [post,setPost]=useState({body:''});
    function getPost(){

        useEffect(
            () => {
              if ((localStorage.getItem("userName")) == 'undefined' || (localStorage.getItem("userName")) == null) {
                console.log('not found');
                navigate("/");
              }
              else {
                let url = `https://jsonplaceholder.typicode.com/posts?id=${param.postId}`;
                fetch(url)
                    .then(response => response.json())
                    .then(response => {
                        setPost(response[0]);
                        console.log(response)
                    })
                    let [text, setText] = useState("show comments");
              }
            }, []
          );
        
        
    }
    useEffect(()=>{getPost()},[])
    
    function getData()
    {
        
        }
    useEffect(()=>{getData()},[])
    function show(){
        if(text=="show comments"){
            setArr(comments);
            setText("hide comments")    
        }
        else{
            setArr([])
            setText("show comments")    
        }     
    }  
    return (
        <div>
            {/* {console.log(post.body)} */}
            <h2> {post.body}</h2>
<button onClick={show}>{text}</button>
{
    arr.map((comment)=>(
        <div key={comment.id}>
    <h3 >{comment.body}</h3>
<h5>{comment.name} <br/> {comment.email}</h5>
    </div>
))}
    </div>
    );
}
export default Post;
