import React, { useState } from 'react'
import Main from './components/Main'
import Albums from './components/Albums';
import Posts from './components/Posts';
import Todos from './components/Todos';
import Info from './components/info';
import Photo from './components/Photo';
import Post from './components/Post';
import { Routes, Route ,Router,HashRouter} from 'react-router-dom';

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [album, setAlbumId] = useState();
    const [post, setPost] = useState();
    function hundleCurrent(data) {
        setCurrentUser(data);
    }
    function hundleAlbum(data) {
        setAlbumId(data);
    }
    function hundlePost(data) {
        setPost(data);
    }
    return (
        <div>
            
                <Routes>
                     <Route exact path="/Main" element={<Main />} />
                       <Route exact path="/:userID/Posts" element={<Posts currentUser={currentUser} hundlePost={hundlePost} />} />
                </Routes>
                
        </div>
    );
}
export default App
