import Game from './game';

class MainGames extends Component {
    state = {
        gamesArr: [],

    }

    gamer= {
        name: '', score: 0
    }

addPlayer=(e)=>{
// this.gamer.name=name;
// this.setState({ gamesArr: [...this.state.gamesArr, this.gamer] });
// console.log(this.state.gamesArr);
this.props.setText(e.target.value);

}
    render() {
        return (
            <React.Fragment>
                <button onClick={() => this.addPlayer}>הוספת שחקן</button>
                <input type="text" id="namePlayer"></input>
                {this.state.gamesArr.map(player => (
                    <Game
                        name={this.state.gamer.name}
                        score={this.state.gamer.score}
                    />
                ))}
            </React.Fragment>
        );
    }
}
export default MainGames;
