import React, { Component } from 'react';
import Game from './game';

class MainGames extends Component {
    state = {
        current: -1,
        gamesArr: [],
        currentInput: '',
        counterKey: 0
        
    }
    cheackTheTextBox = () => {
        if (this.state.currentInput === "") {
            alert("please enter your name")
        }
        else {
            this.addPlayer(this.state.currentInput)
        }
    }

    addPlayer = (name) => {
        let tempGamer = { name: name, buttunState: false }
        this.setState({ gamesArr: [...this.state.gamesArr, tempGamer] });
        this.setState({ currentInput: "" })
    }

     startTheGame = () => {
        if (this.state.gamesArr.length === 0) {
            alert("there is no gamer")
        }
        else {
            this.changeTurn(0, 0);
            let tempArr = this.state.gamesArr
            this.setState({ gamesArr: tempArr });
        }
    }

    changeTurn = (prev, current) => {
        let players = [...this.state.gamesArr];
        players[prev].buttunState = false;
        players[current].buttunState = true;
        this.setState({ gamesArr: players, current: current })
    }

    funcStatusOfButtons = () => {
        let prev = this.state.current;
        let current = this.state.gamesArr.length - 1 === prev ? 0 : prev + 1;
        this.changeTurn(prev, current);
    }

    funcDeleteGamer = (name) => {
        let temp;
        let i = 0;
        this.state.gamesArr.map(index => {
            if (index.name === name) {
                temp = i;
            }
            i++
        });
        temp=(temp+1)%this.state.gamesArr.length
        this.changeTurn(temp,temp)
        this.setState({ gamesArr: this.state.gamesArr.filter(p => p.name != name) })
    }

    render() {
        return (
            <React.Fragment>
                <h1>Get to 100!!!</h1>
                <button onClick={this.cheackTheTextBox}>Add Gamer</button>
                <input type="text" value={this.state.currentInput} onChange={e => this.setState({ currentInput: e.target.value })}></input>
                <button onClick={this.startTheGame}>start game</button>
                {this.state.gamesArr.map((player) => (
                    <Game
                        key={thus.statecounterKey}
                        name={player.name}
                        buttunState={player.buttunState}
                        buttunStateProps={player.buttunState}
                        funcButtun={this.funcStatusOfButtons}
                        playerArr={this.state.gamesArr}
                        funcDelete={this.funcDeleteGamer}
                    />
                ))}
            </React.Fragment>
        );
    }
}
export default MainGames;
