import React from 'react';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Loader from './Loader';

const AddPost = ({ handlePostUpdate }) => {
    let params=useParams();
    const [isLoading, setIsLoading] = useState(false); //true when the server is up
    const [open, setOpen] = React.useState(false);
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')

    const onFinish = async () => {
      setIsLoading(true)
      try {
        const post = {
          title: title,
          body: body,
          userId: params.userID,
        };
        const response = await fetch('https://jsonplaceholder.typicode.com/posts', {
          method: 'POST',
          body: JSON.stringify(post),
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
          },
        });
  
        if (!response.ok) {
          throw new Error(`HTTP error ${response.status}`);
        }
  
        const newPost = await response.json();
        handlePostUpdate(newPost); // Call the function to update posts
        handleClose();
      } catch (error) {
        console.error('ERROR:', error); // Log the error to the console
        // Optionally, display an error message to the user here
      }
      setIsLoading(false);
    };
    
    const handleClickOpen = () => {
      setOpen(true);
    };
  
  

    const handleClose = () => {
      setOpen(false);
    };
  
    return (
     
        <div className="addPostForm">
            <React.Fragment>
      <Button variant="outlined" onClick={handleClickOpen}>
        Add new post
      </Button>
     
      <Dialog open={open} onClose={handleClose}  onFinish={onFinish}>
        <DialogTitle>Add new post</DialogTitle>
        <DialogContent>
          <DialogContentText>
            fill in the titlw and the body, that you want to share in your post
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="title"
            type="text"
            fullWidth
            variant="standard"
            value={title}
          onChange={e => {
            setTitle(e.target.value)
          }}
          />
          <TextField
            autoFocus
            margin="dense"
            id="body"
            label="body"
            type="text"
            fullWidth
            variant="standard"
            value={body}
          onChange={e => {
            setBody(e.target.value)
          }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={onFinish}>Subscribe</Button>
          {isLoading&&<Loader/>}
        </DialogActions>
      </Dialog>
    </React.Fragment>
      </div>
     
    );
  };
  
  export default AddPost
