import React, { useEffect, useState } from 'react';
import SinglePhoto from './SinglePhoto';
import { useParams } from 'react-router';
function Photo(props){
    let param=useParams();
    console.log(param.albumId);
    let [arr, setPhoto] = useState([]);
let [photoId,setPhotoId]=useState(((props.album.id*50)-50)+1);
let tmpPhotoArr=[];
        useEffect(function  getData()
        {
            let url = `https://jsonplaceholder.typicode.com/photos?_page=${photoId}`;
            fetch(url)
                .then(response => response.json())
                .then(response => {
                    tmpPhotoArr=arr;
                    tmpPhotoArr.push(response);
                    setPhoto(tmpPhotoArr);
                    console.log(arr);
                })
            },[photoId]);
        function changeId(){
            console.log(photoId ,props.album.id)
            if(photoId<(props.album.id*50)){
                setPhotoId(photoId+10);
            }
            else{
                alert("you finish load all the album's pictures");
            }
        }
return(
<div>
<h1>album {props.album.id}</h1>
{
arr.map((photo)=>(
 <SinglePhoto single={photo} key={photo[0].id}>
     </SinglePhoto>
))} 
<button onClick={changeId} id="loadedButton" >load more</button>
</div>
);
}
export default Photo;
