import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';

function Main() {
  const [text, setText] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const url = 'https://jsonplaceholder.typicode.com/users';
      const response = await fetch(url);
      const data = await response.json();
      setUsers(data);
      setFilteredUsers(data); // Initialize filteredUsers with all users
    }

    fetchData();
  }, []);

  useEffect(() => {
    const filtered = users.filter((user) =>
      user.name.toLowerCase().includes(text.toLowerCase()) ||
      user.email.toLowerCase().includes(text.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [users, text]);

  return (
    <div className="table">
      <h1>users</h1>
      <input
        type="text"
        placeholder="search"
        onChange={(event) => setText(event.target.value)}
      />
      <TableContainer component={Paper} sx={{ maxWidth: 500, maxHeight: 400 }}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow >
              <div className="heads">
              <TableCell align="center">name</TableCell>
              <TableCell align="center">email@</TableCell>
              <TableCell align="center">company-name</TableCell>
              <TableCell align="center">posts</TableCell>
              </div>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredUsers.map((user) => (
              <TableRow
                key={user.name}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {user.name}
                </TableCell>
                <TableCell align="center">{user.email}</TableCell>
                <TableCell align="center">{user.company.name}</TableCell>
                <Link to={`/${user.id}/Posts`}>show posts</Link>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}

export default Main;
