import React, { Component } from 'react';

class Game extends Component {
    state = {
        number:99,// Math.floor(Math.random() * 100),
        counterSteps: 0,
        arrScore: []
    }

    funcButtun(num) {
        let tempNumPlus = 0;
        switch (num) {
            case '+1':
                tempNumPlus = this.state.number + 1;
                break;
            case '-1':
                tempNumPlus = this.state.number - 1;
                break;
            case '*2':
                tempNumPlus = this.state.number * 2;
                break;
            case '/2':
                tempNumPlus = this.state.number / 2;
                break;

        }
        let tempCounterSteps = this.state.counterSteps + 1
        this.setState({ number: tempNumPlus, counterSteps: tempCounterSteps });
        if (tempNumPlus === 100)
        {
            this.setState({ finished: true });
        } else {
            this.props.funcButtun()
        }
    }

    checkNumber = () => {
        if (this.state.number === 100) {
            this.setState({ arrScore: [...this.state.arrScore, this.state.counterSteps] });
            this.setState({ counterSteps: 0 });
            this.setState({ number: Math.floor(Math.random() * 100) })
        }
    }

    render() {

        let classes = 'btn-'
        classes += (this.state.number === 100) ? "show" : "notShow"
        return (
            <div className='divGame'>
                <h3>Gamer == {this.props.name}</h3>
                <h3>Number == {this.state.number}</h3>
                <h3>steps == {this.state.counterSteps}</h3>
                <div>
                    <button onClick={() => this.funcButtun('+1')} disabled={this.state.number == 100 || this.props.buttunState == false}>+1</button>
                    <button onClick={() => this.funcButtun('-1')} disabled={this.state.number == 100 || this.props.buttunState == false}>-1</button>
                    <button onClick={() => this.funcButtun('*2')} disabled={this.state.number == 100 || this.props.buttunState == false}>*2</button>
                    <button onClick={() => this.funcButtun('/2')} disabled={this.state.number == 100 || this.props.buttunState == false}>/2</button>
                </div>

                <button onClick={() => this.checkNumber()} className={classes} >new game</button>
                <button onClick={() => this.props.funcDelete(this.props.key)} className={classes} >quit</button>
                <h3>{this.props.name}'s score == {this.state.arrScore.join(',')}</h3>
            </div>
        );
    }
}
export default Game;
