import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';


function Posts(props) {
    let [posts, setPosts] = useState([]);


    
    function getData() {
        let userId = (JSON.parse(localStorage.getItem('userName')))[0].id;
        let url = `https://jsonplaceholder.typicode.com/posts?userId=${userId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                setPosts(response);
            })
    }
    useEffect(() => { getData() }, [])
    return (
        <div>{
            posts.map((post) => (
                <div key={post.id}>
                    <Link to={`/Main/posts/post/${post.id}`} onClick={() => props.hundlePost(post)}>{post.title}</Link>
                </div>
            ))}
        </div>
    );
}
export default Posts;
