import React, { useState, useEffect } from 'react';
import Main from './components/Main';
import Posts from './components/Posts';
import { Routes, Route, Router, HashRouter, useParams } from 'react-router-dom';
import AddPost from './components/AddPost';
import './style.css'


function App() {
    let [posts, setPosts] = useState([]);
    const { userID } = useParams();
    const [isLoading, setIsLoading] = useState("");
    
    const  getFetchPosts=(IDuser) =>{
      
      setIsLoading(true); // Set loading state to true
      console.log(isLoading,"infetch")
    //this function now accept the userId to return his posts
           let url = `https://jsonplaceholder.typicode.com/posts?userId=${IDuser}`;
                 fetch(url)
                .then(response => response.json())
                .then(response => {
                    setPosts(response);
                })
                setIsLoading(false); // Set loading state to false
                return posts;
   
  }

  function handlePostUpdate (newPost) {
      // This function now accepts a new post as an argument
      setPosts([...posts, newPost]); // Append new post to array
  };
  


  return (
    <div>
      <div className="header">
  <h2>post- thats ours...</h2>
</div>
      <div className="body">
      <Routes>
        <Route path="/" element={<Main  />} />
        <Route
          path="/:userName/:userID/Posts"
          element={
            <div>
              <Main  />
              <Posts getFetchPosts={getFetchPosts} postArr={posts} isLoading={isLoading}/>
              <AddPost handlePostUpdate={handlePostUpdate}  /> 
            </div>
          }
        />
      </Routes>
      </div>
    </div>
  );
}

export default App;
