import React from 'react';
import {useNavigate} from "react-router-dom";
import {useEffect, useState } from 'react';


function Info(){
    
    const [currentUser, setCurrentUser] = useState({id:'',name:'',address:{street:'',suite:'',city:''},email:'',phone:'',website:'',company:{name:'',catchPhrase:'',bs:''}});
    let navigate = useNavigate();
    useEffect(
      () => {
        if ((localStorage.getItem("userName")) == 'undefined' || (localStorage.getItem("userName")) == null) {
          console.log('not found');
          navigate("/");
        }
        else {
            setCurrentUser((JSON.parse(localStorage.getItem('userName')))[0]);
        }
      }, []
    );
    
 return(
<div className="infoText">
<h3>name: {currentUser.name}</h3>
<h3>address: {currentUser.address.street} {currentUser.address.suite} {currentUser.address.city}</h3>
<h3>email: {currentUser.email}</h3>
<h3>phone: {currentUser.phone}</h3>
<h3>website: {currentUser.website}</h3>
<h3>company name: {currentUser.company.name}</h3>
<h3>company catchPhrase: {currentUser.company.catchPhrase}</h3>
<h3>company bs: {currentUser.company.bs}</h3>

</div>
);
}
export default Info;
