import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router';
import { PoweroffOutlined } from '@ant-design/icons';
import { Button, Flex } from 'antd';
import Loader from './Loader';
function Posts({getFetchPosts,postArr,isLoading}) {
  let [posts, setPosts] = useState([]);
  let [load,setLoad]=useState(false);
  const params = useParams();

  const [loadings, setLoadings] = useState([]);
  const enterLoading = (index) => {
    setLoadings((prevLoadings) => {
      const newLoadings = [...prevLoadings];
      newLoadings[index] = true;
      return newLoadings;
    });
    setTimeout(() => {
      setLoadings((prevLoadings) => {
        const newLoadings = [...prevLoadings];
        newLoadings[index] = false;
        return newLoadings;
      });
    }, 6000);
  };

  
  useEffect(() => {
    console.log(isLoading)
    if(isLoading){
      setLoad(true);
    }
      let postsArray=getFetchPosts(params.userID);//take the posts from app, and return to screen
      setPosts(postsArray); 
  }, [params.userID,posts,load]);

  return (
    
      <div className="posts">
        <h1 className="h1">{params.userName}'s Posts</h1>
        {load&&<Loader/>}
        
        <div className="grid-container">
        {postArr.map((post) => (
          <div key={post.id} className="singlePost">
            <h3>{post.title}</h3>
            <p>{post.body}</p>
          </div>
        ))}
        </div>
        
      </div>
    
  );
}

export default Posts;
