import React, { useEffect, useState } from 'react';


function Photo(props){
    let [arr, setPhoto] = useState([]);
let [photoId,setPhotoId]=useState(((props.album.id*50)-50)+1);
console.log(photoId);
let tmpPhotoArr=[];
    const  getData=()=>
    {
        let url = `https://jsonplaceholder.typicode.com/photos?id=${photoId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                console.log(response);
                tmpPhotoArr=arr;
                tmpPhotoArr.push(response);
                setPhoto(tmpPhotoArr);
            })
        }
        useEffect(()=>{getData()},[])
return(
<div>
<h1>album {props.album.id}</h1>
 <button onClick={()=>setPhotoId(photoId+1)}>load more</button>
{
arr.map((photo)=>(
    <div key={photo.id}>
<h1>{photo.title}</h1>
<img src={photo.url} height="150px" width="150px"></img>
<img src={photo.thumbnailUrl} height="150px" width="150px"></img>
</div>
))} 

</div>
);
}
export default Photo;
