import React, { useState } from 'react'
import Main from './components/Main'
import Posts from './components/Posts';
import { Routes, Route ,Router,HashRouter} from 'react-router-dom';
import AddPost from './components/AddPost';

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [album, setAlbumId] = useState();
    const [post, setPost] = useState();
    function handleCurrent(data) {
        setCurrentUser(data);
    }
    function handleAlbum(data) {
        setAlbumId(data);
    }
    function hundlePost(data) {
        setPost(data);
    }
    return (
        <div>
            
            <Routes>
        <Route
          path="/"
          element={<Main handleCurrent={handleCurrent} />}
        />
        <Route
          path="/:userID/Posts"
          element={
            <div>
              <Main handleCurrent={handleCurrent} />
              <Posts currentUser={currentUser} handlePost={handlePost} />
            </div>
          }
        />
        <Route
          path="/:userID/Posts/addPost"
          element={
            <div>
              <Main handleCurrent={handleCurrent} />
              <Posts currentUser={currentUser} handlePost={handlePost} />
              <AddPost currentUser={currentUser} handlePost={handlePost} />
            </div>
          }
        />
      </Routes>
                
        </div>
    );
}
export default App
