import React, { Component } from 'react';



class Game extends Component {
    state = {
        number: Math.floor(Math.random() * 100),
        counterSteps: 0
    }

    funcButtun(num) {
        switch (num) {
            case '+1':
                let tempNumPlus = this.state.number + 1;
                this.setState({ number: tempNumPlus });
                let tempCounterSteps = this.state.counterSteps+1
                this.setState({ counterSteps: tempCounterSteps });
               
                break;
            case '-1':
                let tempNumMinus = this.state.number - 1;
                this.setState({ number: tempNumMinus });

                break;
            case '*2':
                let tempNumMulty = this.state.number * 2;
                this.setState({ number: tempNumMulty });

                break;
            case '/2':
                let tempNumDivide = this.state.number / 2;
                this.setState({ number: tempNumDivide });
                // floor maybe?
                break;

        }
        let tempCounterSteps = this.state.counterSteps+1
        this.setState({ counterSteps: tempCounterSteps });
    }

    render() {
        return (
            <React.Fragment>
                <h2>Hello {this.props.name}</h2>
                <h2>{this.state.number}</h2>
                <button onClick={() => this.funcButtun('+1')} >+1</button>
                <button onClick={() => this.funcButtun('-1')}>-1</button>
                <button onClick={() => this.funcButtun('*2')}>*2</button>
                <button onClick={() => this.funcButtun('/2')}>/2</button>
                <h3>steps: {this.state.counterSteps}</h3>
            </React.Fragment>
        );
    }
}
export default Game;
