import React, { Component } from 'react';
import Game from './game';

class MainGames extends Component {
    state = {
        gamesArr: []
    }

    gamer = {
        name: '', score: 0
    }

    tempFunc = () => {
        let name = document.getElementById("namePlayer").value
        console.log("name", name);
        if (name === "") {
            alert("please enter your name")
        }
        else { 
            this.addPlayer(name) }
    }

    addPlayer = (name) => {
        let tempGamer = { name: name, score: 0 }
        console.log(tempGamer);
        this.setState({ gamesArr: [...this.state.gamesArr, tempGamer] });
        document.getElementById("namePlayer").value = " ";
    }

    render() {
        console.log(this.state.gamesArr);
        return (
            <React.Fragment>
                <button onClick={this.tempFunc}>הוספת שחקן</button>
                {/* <textarea ></textarea> */}
                <input type="text" id="namePlayer"></input>

                {this.state.gamesArr.map(player => (



                    <Game
                        name={player.name}
                        score={player.score}
                    />
                ))}

            </React.Fragment>
        );
    }
}
export default MainGames;
