import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import {useNavigate} from "react-router-dom";

function Post() {
    let param=useParams();
    let [comments, setComments] = useState([]);
    let [arr, setArr] = useState([]);
    let [post,setPost]=useState({body:''});
    let [text, setText] = useState("show comments");
        useEffect(
            () => {
              if ((localStorage.getItem("userName")) == 'undefined' || (localStorage.getItem("userName")) == null) {
                console.log('not found');
                navigate("/");
              }
              else {
                let url = `https://jsonplaceholder.typicode.com/posts?id=${param.postId}`;
                fetch(url)
                    .then(response => response.json())
                    .then(response => {
                        setPost(response[0]);
                        console.log(response)
                    });
                    fetch(`https://jsonplaceholder.typicode.com/comments?postId=${param.postId}`)
                        .then(response => response.json())
                        .then(response => {
                            setComments(response);
                        });
              }
            }, []
          );
    function show(){
        if(text=="show comments"){
            setArr(comments);
            setText("hide comments")    
        }
        else{
            setArr([])
            setText("show comments")    
        }     
    }  
    return (
        <div>
            {/* {console.log(post.body)} */}
            <h2> {post.body}</h2>
<button onClick={show}>{text}</button>
{
    arr.map((comment)=>(
        <div key={comment.id}>
    <h3 >{comment.body}</h3>
<h5>{comment.name} <br/> {comment.email}</h5>
    </div>
))}
    </div>
    );
}
export default Post;
