import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router';
import AddPost from './AddPost';

export function Posts() {
  const [posts, setPosts] = useState([]);
  const params = useParams();

  useEffect(() => {
    async function getFetch() {
      const url2 = `https://jsonplaceholder.typicode.com/posts?userId=${params.userID}`;
      const response2 = await fetch(url2);
      const b = await response2.json();
      setPosts(b);
    }
    getFetch();
  }, [params.userID,posts]);

  
 
  return (
    <div>
      <div>
        <h1 className="h1">user {params.userID}s Posts</h1>
        {posts.map((post) => (
          <div key={post.id} className="LinksMenu">
            <h3>{post.title}</h3>
            <h4>{post.body}</h4>
          </div>
        ))}
        <button>
          <Link to={`/${params.userID}/Posts/addPost`}>add new posts</Link>
        </button>
      </div>
    </div>
  );
}

export default Posts;
