import React from 'react';
import { Button, Form, Input } from 'antd';
import { useParams } from 'react-router';

const AddPost = ({ handlePostUpdate }) => {
    let params=useParams();
    
    const onFinish = (values) => {
      let post = {
        title: values.label,
        body: values.body,
        userId:params.userID,
    };
    
      fetch('https://jsonplaceholder.typicode.com/posts', {
        method: 'POST',
        body: JSON.stringify(post),
        headers: {
          'Content-type': 'application/json; charset=UTF-8',
        },
      })
        .then((response) => response.json())
        .then((post) => {
            handlePostUpdate(post); // Call the function to update posts
          })
    };
  
    return (
      <Form 
      className="formAddingPost"
        name="basic"
        labelCol={{
          span: 8,
        }}
        wrapperCol={{
          span: 16,
        }}
        style={{
          maxWidth: 600,
        }}
        initialValues={{
          remember: true,
        }}
        onFinish={onFinish}
        autoComplete="off"
      >
        <Form.Item
          label="label"
          name="label"
          rules={[
            {
              required: true,
              message: 'Please write your posts label',
            },
          ]}
        >
          <Input />
        </Form.Item>
  
        <Form.Item
          label="body"
          name="body"
          rules={[
            {
              required: true,
              message: 'Please input posts body',
            },
          ]}
        >
          <Input />
        </Form.Item>
  
        <Form.Item
          name="remember"
          valuePropName="checked"
          wrapperCol={{
            offset: 8,
            span: 16,
          }}
        >
        </Form.Item>
  
        <Form.Item
          wrapperCol={{
            offset: 8,
            span: 16,
          }}
        >
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  };
  
  export default AddPost
