import React, { useEffect, useState } from 'react';


function Post() {
    let param=useParams();
    let [comments, setComments] = useState([]);
    let [arr, setArr] = useState([]);
    let [text, setText] = useState("show comments");
    function getData()
    {
        let url = `https://jsonplaceholder.typicode.com/comments?postId=${param.postId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                setComments(response);
            })
        }
    useEffect(()=>{getData()},[])
    function show(){
        if(text=="show comments"){
            setArr(comments);
            setText("hide comments")    
        }
        else{
            setArr([])
            setText("show comments")    
        }     
    }  
    return (
        <div>
            <h2> {props.post.body}</h2>
<button onClick={show}>{text}</button>
{
    arr.map((comment)=>(
        <div key={comment.id}>
    <h3 >{comment.body}</h3>
<h5>{comment.name} <br/> {comment.email}</h5>
    </div>
))}
    </div>
    );
}
export default Post;
