import React from 'react';
import { Link ,Outlet} from 'react-router-dom';
import {useEffect, useState } from 'react';
import {useNavigate} from "react-router-dom";
import '../All.css'

function Main() {
    function exitUser()
    {
        localStorage.removeItem("userName");
    }
    let navigate= useNavigate();
    const [currentU,setCurrent] =useState({});
    useEffect(
        () => {
          if ((localStorage.getItem("userName")) == 'undefined' || (localStorage.getItem("userName")) == null) {
            console.log('not found');
            navigate("/");
          }
          else {
             setCurrent(JSON.parse(localStorage.getItem("userName"))[0]);
          }
        }, []
      );
    return (
     <div className="body">
                
                <nav>
                <h1 className="userName">{currentU.name}</h1>
                  <ul className="navLink">
                <li><Link to="Info" className="menuLink">info</Link></li>
                <li><Link to="Posts" className="menuLink">Posts</Link></li>
                <li><Link to="Albums" className="menuLink">Albums</Link></li>
                <li><Link to="Todos" className="menuLink">Todos</Link></li>
                <li><Link to="/" onClick={exitUser} className="menuLink">LogOut</Link></li>
             
                </ul>
                </nav>
                <Outlet/>
            </div>

    );
}
export default Main;
