        let url = `https://jsonplaceholder.typicode.com/albums?userId=${userId}`;
        console.log(url);
        fetch(url)
            .then(response => response.json())
            .then(response => console.log(response))
            setAlbums(response);
    
    }
    
return(
<div>
    
<a onClick={getData}>show data</a>
{
response.map((album)=>(
    <span>{Albums.title}</span>
))}
</div>
);
}
