import React, { useEffect, useState } from 'react';


function Post(props) {
    
    let [arr, setPhoto] = useState([]);

    function getData()
    {
        const albumId=props.post.id;
        let url = `https://jsonplaceholder.typicode.com/comments?postId=${albumId}`;
        fetch(url)
            .then(response => response.json())
            .then(response => {
                setPhoto(response);
            })
        }

    useEffect(()=>{getData()},[])
    return (
        <div>
            <p> {props.post.body}</p>

        </div>
    );
}
export default Post;
